pm.test('Dados batem com o enviado', function(){
  pm.expect(title).to.eql(pm.variables.get('book_title'));
  pm.expect(description).to.eql(pm.variables.get('book_description'));
  pm.expect(Number(pageCount)).to.eql(Number(pm.variables.get('book_pageCount')));
  pm.expect(String(publishDate)).to.include(String(pm.variables.get('book_publishDate')).slice(0,19));
});

pm.test('Dados persistidos batem com o enviado', function(){
  pm.expect(title).to.eql(pm.variables.get('book_title'));
  pm.expect(description).to.eql(pm.variables.get('book_description'));
  pm.expect(Number(pageCount)).to.eql(Number(pm.variables.get('book_pageCount')));
  pm.expect(String(publishDate)).to.include(String(pm.variables.get('book_publishDate')).slice(0,19));
});
